svg:
{{- /* tile size */ -}}
{{- $tw := 100 }}
{{- $th := 80 }}

{{- /* number of tiles */ -}}
{{- $nx := randi 3 5 }}
{{- $ny := randi 3 5 }}

{{- /* pattern parameters */ -}}
{{- $pw := $nx | times $tw }}
{{- $ph := $ny | times $th }}








  {{- range $lx := grid $nx }}
  {{- range $ly := grid $ny }}

    {{- $col := pick "#222" "#ddd" }}
    {{- $opa := randf 0.01 0.14 | round 2 }}

    {{- range $x := $lx }}
    {{- range $y := $ly }}
      {{- $dx := $x | times $tw | round 2 }}
      {{- $dy := $y | times $th | round 2 }}

    {{- end }}
    {{- end }}

  {{- end }}
  {{- end }}



{{- if gt .Opacity 0.0 }}

{{- end }}
