svg:
{{- /* tile size */ -}}
{{- $tw := 70 }}
{{- $th := 70 }}

{{- /* number of tiles */ -}}
{{- $nx := randi 3 5 }}
{{- $ny := randi 3 5 }}

{{- /* pattern parameters */ -}}
{{- $pw := $nx | times $tw }}
{{- $ph := $ny | times $th }}







  {{- range $lx := grid $nx }}
  {{- range $ly := grid $ny }}

    {{- $fill1 := pick "#222" "#ddd" }}
    {{- $fill2 := pick "#222" "#ddd" }}
    {{- $fill3 := pick "#222" "#ddd" }}
    {{- $opacity1 := randf 0.01 0.14 | round 2 }}
    {{- $opacity2 := randf 0.01 0.14 | round 2 }}
    {{- $opacity3 := randf 0.01 0.14 | round 2 }}

    {{- range $x := $lx }}
    {{- range $y := $ly }}
      {{- $dx := $x | times $tw | round 2 }}
      {{- $dy := $y | times $th | round 2 }}
      {{- if eq (pick 1 2) 1 }}

      {{- else }}


      {{- end }}
    {{- end }}
    {{- end }}

  {{- end }}
  {{- end }}



{{- if gt .Opacity 0.0 }}

{{- end }}
