svg:
{{- /* tile size */ -}}
{{- $tw := 35 }}
{{- $th := 35 }}

{{- /* number of tiles */ -}}
{{- $nx := randi 7 11 }}
{{- $ny := randi 7 11 }}

{{- /* pattern parameters */ -}}
{{- $pw := $nx | times $tw }}
{{- $ph := $ny | times $th }}




  {{- range $x := (upto $nx) }}
    {{- $rw := randi 14 28 }}
    {{- $rx := randi 0 7 }}
    {{- $dx := $x | times $tw | plus $rx }}
    {{- $fill := pick "#222" "#ddd" }}
    {{- $opacity := randf 0.01 0.14 | round 2 }}

  {{- end }}

  {{- range $y := (upto $ny) }}
    {{- $rh := randi 14 28 }}
    {{- $ry := randi 0 7 }}
    {{- $dy := $y | times $th | plus $ry }}
    {{- $fill := pick "#222" "#ddd" }}
    {{- $opacity := randf 0.01 0.14 | round 2 }}

  {{- end }}




{{- if gt .Opacity 0.0 }}

{{- end }}
