svg:
{{- /* tile size */ -}}
{{- $tw := 35 }}
{{- $th := 35 }}

{{- /* number of tiles */ -}}
{{- $xn := randi 7 11 }}
{{- $yn := randi 7 11 }}

{{- /* pattern parameters */ -}}
{{- $pw := $xn | times $tw }}
{{- $ph := $yn | times $th }}



  {{- if gt .Opacity 0.0 }}

  {{- end }}

  {{- range $x := (upto $xn) }}
  {{- $rw := randi 14 28 }}
  {{- $rx := randi 0 7 }}
  {{- $dx := $x | times $tw | plus $rx }}
  {{- $fill := pick "#222" "#ddd" }}
  {{- $opacity := randf 0.01 0.14 | round 2 }}

  {{- end }}

  {{- range $y := (upto $yn) }}
  {{- $rh := randi 14 28 }}
  {{- $ry := randi 0 7 }}
  {{- $dy := $y | times $th | plus $ry }}
  {{- $fill := pick "#222" "#ddd" }}
  {{- $opacity := randf 0.01 0.14 | round 2 }}

  {{- end }}
