svg:
{{- /* tile size */ -}}
{{- $tw := randi 70 105 }}
{{- $th := $tw }}

{{- /* number of tiles */ -}}
{{- $xn := randi 3 5 }}
{{- $yn := randi 3 5 }}

{{- /* pattern parameters */ -}}
{{- $pw := $xn | times $tw }}
{{- $ph := $yn | times $th }}




  {{- if gt .Opacity 0.0 }}

  {{- end }}

  {{- $lx := var "" }}
  {{- $ly := var "" }}
  {{- range $x := (upto $xn) }}
  {{- range $y := (upto $yn) }}
  {{- $fill := pick "#222" "#ddd" }}
  {{- $opacity := randf 0.01 0.14 | round 2 }}
  {{- if (eq $x 0) }}
    {{- list 0 $xn | set $lx }}
  {{- else }}
    {{- list $x | set $lx }}
  {{- end }}
  {{- if (eq $y 0) }}
    {{- list 0 $yn | set $ly }}
  {{- else }}
    {{- list $y | set $ly }}
  {{- end }}


    {{- range $rx := $lx }}

    {{- range $ry := $ly }}

    {{- $dx := $rx | times $tw | round 2 }}
    {{- $dy := $ry | times $th | round 2 }}

    {{- end }}
    {{- end }}
  {{- end }}
  {{- end }}
